mapper.update:
UPDATE Board
SET modifierName = #{modifierName}, title = #{title}, content = #{content}, updateDate = now()
WHERE id = #{id}

UPDATE Board
SET deleteYn = 'Y'
WHERE id = #{id}

mapper.select:
SELECT *
FROM Board
WHERE deleteYn = 'N'
ORDER BY id DESC
LIMIT #{amount}
OFFSET #{skip}

mapper.update:
UPDATE Board
SET commentCnt = commentCnt + 1
WHERE id = #{id}

UPDATE Board
SET commentCnt = commentCnt - 1
WHERE id = #{id}
